mapper.select:
SELECT *
FROM user
WHERE email = #{email}
  and password = #{password}

mapper.update:
UPDATE user
SET status = 0
WHERE id = #{id}

UPDATE user
SET password   = #{password},
    updated_by = #{updatedBy},
    updated_at = #{updatedAt}
WHERE id = #{id}

UPDATE user
SET verification_key = #{verificationKey},
    updated_by       = #{updatedBy},
    updated_at       = #{updatedAt}
WHERE id = #{id}

UPDATE user
SET followed = followed + 1
WHERE id = #{id}

UPDATE user
SET following  = following +1
WHERE id =#{id}

UPDATE user
SET followed = followed -1
WHERE id = #{id}

UPDATE user
SET following  = following -1
WHERE id =#{id}
